def compute(xs: Int) = {
  def twice(y: Int) = 2*y
  def inc(y: Int) = y+1
  twice(xs) + inc(xs)
}

println(compute(23))

class Decorator(left: String, right: String) {
    def decorate(x: Int) :String = left + x.toString + right
}

def applyDecorator[X](f: X => Int, decorate:Int => String) = {
    x:X => decorate(f(x))
}

val decorator = new Decorator("[", "]")
val calculator = applyDecorator((n:Int) => 2*n, decorator.decorate)
println(calculator(23))

