def filter(xs: List[Int], threshold: Int) = {
  def process(ys: List[Int]): List[Int] =
    if (ys.isEmpty) ys
    else if (ys.head < threshold) ys.head :: process(ys.tail)
    else process(ys.tail)
  process(xs)
}

println(filter(List(1, 9, 2, 8, 3, 7, 4), 5))

def reduce(agg:(Int,Int), value:Int) = (agg._1 + value, agg._2 + 1)
def combine(left:(Int,Int),right:(Int,Int)) = (left._1 + right._1 , left._2 + right._2)
val zero = (0,0)

val agg = range.aggregate(zero)(reduce, combine)
val avg = agg._1.toFloat / agg._2.toFloat

