import org.apache.spark.sql.DataFrame

implicit class ZeppelinOutput(df:DataFrame) {
    def toZeppelin(limit:Int = -1)  = {
        println("%table")
        println(df.schema.map(_.name).mkString("\t"))
        val data = if (limit <= 0) df else df.limit(limit)
        data.collect.foreach(row => println(row.mkString("\t")))
    }
}

import org.apache.spark.sql.Row
import org.apache.spark.sql.types._

// Create a UDF for creating a pseudo random first name. Use the given seed to initialize a Random number generator, so we get deterministic results.
val randomFirstName = sqlContext.udf.register("randomFirstName", (seed:Long) => {
    val rnd = new scala.util.Random(100*seed)
    val firstNamesMale = firstNamesMaleBc.value
    val firstNamesFemale = firstNamesFemaleBc.value
    val numFirstNamesMale = firstNamesMale.length
    val numFirstNamesFemale = firstNamesFemale.length

    if (rnd.nextBoolean()) 
        firstNamesMale(rnd.nextInt(numFirstNamesMale))
    else
        firstNamesFemale(rnd.nextInt(numFirstNamesFemale))
})

// Create a UDF for creating a pseudo random last name. Use the given seed to initialize a Random number generator, so we get deterministic results.
val randomLastName = sqlContext.udf.register("randomLastName", (seed:Long) => {
    val rnd = new scala.util.Random(100*seed + 1)
    val lastNames = lastNamesBc.value
    val numLastNames = lastNames.length

    lastNames(rnd.nextInt(numLastNames))
})

// Create lets say 10,000,000 different names, each having a first_name and a last_name. The values for the first and last name should be 
// generated using the UDFs defined above.
val people = sc.parallelize(1 to 10000000, 100).toDF
    .select($"_1" as "index")
    .select($"index", randomFirstName($"index").as("first_name"), randomLastName($"index").as("last_name"))

val randomSessions = sqlContext.udf.register("randomSession", (seed:Long) => {
    // Create deterministic RNG using given seed
    val rnd = new scala.util.Random(seed)

    // Locally define lognormal distribution using random number generator defined above
    def lognormal(a:Double) : Double = scala.math.exp(a*rnd.nextGaussian()) 

    // Generate CDKs for specific user. Make sure user has at least one CDK
    val numCdks = scala.math.max((14.0*lognormal(0.3)).toInt, 1)
    val cdks = 1 to numCdks map { i => "cdk_" + rnd.alphanumeric.take(32).mkString("") }

    // Generate sessions for user
    val numSessions = (50.0*lognormal(0.4)).toInt
    1 to numSessions flatMap { i:Int => {
        // Create random session ID
        val session = "sesn_" + rnd.alphanumeric.take(32).mkString("")
        // Generate CDK entries for session
        val usedCdks = (2.7*lognormal(0.3)).toInt
        1 to usedCdks map { i => (session, cdks(rnd.nextInt(numCdks))) }
    }}
})

// First create a DataFrame of 200,000 users. We call this DF 'people'. Since we could have double entries, we make the user names
// unique by selecting the entry with the highest 'index' value.
val people = sc.parallelize(1 to 200000, 100).toDF
    .select($"_1" as "index")
    .select($"index", concat(randomFirstName($"index"), lit(" "), randomLastName($"index")).as("name"))
    .groupBy($"name").max("index")
    .select($"name",$"max(index)".as("index"))

// Now for every user create a bunch of sessions using the UDF defined above.
val sessions = people.select($"name", explode(randomSessions($"index")) as "session_data")
    .select($"name", $"session_data._1" as "session", $"session_data._2" as "cdk")
    .cache()

// Let's have a look at the result    
sessions.toZeppelin(12)
